import { useContext, useEffect, useState } from "react";
import CartContext from "../Store/food-context";
import classes from "./Button.module.css";
import Icon from "./Icon";

const Button = (props) => {
  const [btnIsHighlighted, setButtonIsHighlighted] = useState(false);
  const cartCtx = useContext(CartContext);

  useEffect(() => {
    if (cartCtx.items.length > 0) {
      setButtonIsHighlighted(true);
      const timer = setTimeout(() => {}, 300);
    }
  }, []);

  const newClass = `${btnIsHighlighted ? "btn " + "bump" : "btn"}`;

  const itemCount = cartCtx.items.reduce((acc, item) => acc + item.amount, 0);
  return (
    <button className={classes.btn} onClick={props.onClickNow}>
      <Icon></Icon>
      <p>{itemCount}</p>
    </button>
  );
};

export default Button;
