import { useReducer } from "react";
import CartContext from "./food-context";

const defaultValue = {
  items: [],
  totalAmount: 0,
};

const reducer = (action, state) => {
  //   if (action.type === "ADD") {
  //     const updatedAmount =
  //       state.totalAmount + action.item.price * action.item.amount;
  //     const existingCartIndex = state.items.findIndex(
  //       (item) => item.id === action.item.id
  //     );
  //     const existingItem = state.items[existingCartIndex];
  //     let updatedItems;
  //     if (existingItem) {
  //       const updatedItem = {
  //         ...existingItem,
  //         amount: existingItem.amount + action.item.amount,
  //       };
  //       updatedItems = [...state.items];
  //       updatedItems[existingCartIndex] = updatedItem;
  //     } else {
  //       updatedItems = state.items.concat(action.item);
  //     }
  //     return { items: updatedItems, totalAmount: updatedAmount };
  //   }
  if (action.type === "ADD") {
    const updatedAmount =
      state.totalAmount + action.item.price * action.item.amount;
    const existingItemIndex = state.items.findIndex(
      (allItem) => allItem.id === action.item.id
    );
    const existingItem = state.items[existingItemIndex];
    let updatedItems;

    if (existingItem) {
      const updatedItem = {
        ...existingItem,
        amount: existingItem.amount + action.item.amount,
      };
      updatedItems = [...state.items];
      updatedItems[existingItemIndex] = updatedItem;
    } else {
      updatedItems = state.items.concat(action.item);
    }
    return {
      items: updatedItems, //You return an object here because the initial defualtvalue was an object too
      totalAmount: updatedAmount,
    };
  }
  return defaultValue;
};

const CartProvider = (props) => {
  const [cartState, dispatchState] = useReducer(reducer, defaultValue);

  const addItemHandler = (item) => {
    dispatchState({ type: "ADD", item: item });
  };

  const removeItemHandler = (id) => {
    dispatchState({ type: "REMOVE", id: id });
  };

  const cartValue = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItems: addItemHandler,
    removeItems: removeItemHandler,
  };
  return (
    <CartContext.Provider value={cartValue}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
